(()=>{"use strict";document.querySelector(".create-todo").addEventListener("click",(function(){(()=>{localStorage.getItem("todos")||localStorage.setItem("todos",JSON.stringify({}));let e={[Date.now()]:{title:document.querySelector("input[name='title']").value,description:document.querySelector("textarea[name='description']").value}},t=JSON.parse(localStorage.getItem("todos")),o=Object.assign(t,e);localStorage.setItem("todos",JSON.stringify(o))})()})),(()=>{let e=JSON.parse(localStorage.getItem("todos"));for(const[t,o]of Object.entries(e)){let e=document.createElement("div");e.innerHTML=`<h4>${JSON.stringify(o.title)}</h4><p>${JSON.stringify(o.description)}</p>`,document.querySelector(".todos").appendChild(e)}})()})();