(()=>{"use strict";document.querySelector(".create-todo").addEventListener("click",(function(){document.querySelector("input[name='title']").value&&document.querySelector("textarea[name='description']").value&&(()=>{localStorage.getItem("todos")||localStorage.setItem("todos",JSON.stringify({}));let e={[Date.now()]:{title:document.querySelector("input[name='title']").value,description:document.querySelector("textarea[name='description']").value}},t=JSON.parse(localStorage.getItem("todos")),o=Object.assign(t,e);localStorage.setItem("todos",JSON.stringify(o))})()})),(()=>{let e=JSON.parse(localStorage.getItem("todos"));for(const[t,o]of Object.entries(e)){let e=document.createElement("div");e.draggable="true",e.classList.add("todo"),e.innerText=`${JSON.stringify(o.title)}, ${JSON.stringify(o.description)}`,document.querySelector("#todos").appendChild(e)}})()})();